package Literals;

public class Eg2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		

				//binary, octal, hexa
				//binary base 2, prefix 0b, (0 and 1)
				int i = 0b0011;
				System.out.println(i); // 3
				
				//octal base 8, prefix is 0 (0 to 7)
				int j = 01236;
				System.out.println(j);
				
				//hexa base 16, prefix is 0x (a to f, 0 to 9)
				int k = 0xace;
				System.out.println(k);
			}

		
	

}
